import React from 'react';
import { CSSTransition } from 'react-transition-group'

import InputModal from '../../Additional/InputModal/InputModal.jsx'
import ErrorMessage from '../../Additional/ErrorMessage/ErrorMessage.jsx'

import Queue from '../../Additional/Queue.js'
import { randomIntFromInverval } from '../../Additional/Helpers.js'

import './BinarySearchTree.scss'

class BinarySearchTreeNode {
    constructor(val) {
        this.value = val;
        this.left  = null;
        this.right = null;

        this.animations = {
            timeout: 0,
            appear: false
        };
    }
}

export default class BinarySearchTree extends React.Component {
    constructor(props) {
        super(props);

        this.initialStateOperation = 'Initial';

        this.name = 'BinarySearchTree';
        this.key  = 'BST';
        this.root = null;

        this.state = {
            animation1: false,
            animation2: false,
            animation3: false,
            animation4: false,
        };

        this.receiveInput = this.receiveInput.bind(this);

        this.action1 = this.action1.bind(this); // temporary
        this.action2 = this.action2.bind(this); // temporary
        this.action3 = this.action3.bind(this); // temporary
        this.action4 = this.action4.bind(this); // temporary
        this.action5 = this.action5.bind(this); // temporary
        this.action6 = this.action6.bind(this); // temporary
        this.action7 = this.action7.bind(this); // temporary
        this.action8 = this.action8.bind(this); // temporary
        this.action9 = this.action9.bind(this); // temporary
    }

    componentDidMount() {
        // call generate function
    }

    /*static getDerivedStateFromProps(newProps, state) {
        //To-do: async calls and tooltips
        console.log("BST::getDerivedStateFromProps::newProps "  + newProps.operation);
        console.log("BST::getDerivedStateFromProps::state.operation " + state.operation);
        console.log("BST::getDerivedStateFromProps::state.animating " + state.animating);

        const initialStateOperation = 'Initial';

        if ( state.animating ) {
            if ( state.operation === initialStateOperation ) {
                return { operation: state.operation, animating: false };
            }
            else {
                return { operation: state.operation };
            }
        }
        else if ( state.operation !== newProps.operation ) {
            return { operation: newProps.operation, animating: true };
        }
        else {
            return null;
        }
    }*/

    insertNumElements(elements) {
        for (let i = 0; i < elements; ++i) {
            let val = randomIntFromInverval(1, 150);
            if ( this.insert( val ) ) {
                if ( this.height() > 5 ) {
                    this.root = this.remove( val );
                    --i;
                }
            }
            else {
                --i;
            }
        }
    }

    reset() {
        this.root = null;
    }

    height(node = this.root) {
        if (node == null) return 0;
        return Math.max( this.height(node.left), this.height(node.right) ) + 1;
    }

    insert(val) {
        if (this.root == null) {
            this.root = new BinarySearchTreeNode(val);
            return true;
        }
        
        let curr = this.root;

        while (true) {
            if (curr.value === val) {
                //tree contains key already
                return false;
            }
            else if (curr.value < val) {
                if(curr.right == null) {
                    curr.right = new BinarySearchTreeNode(val);
                    return true;
                }
                curr = curr.right;
            }
            else {
                if(curr.left == null) {
                    curr.left = new BinarySearchTreeNode(val);
                    return true;
                }
                curr = curr.left;
            }
        }
    }

    remove(val, node = this.root) {
        if ( node === null ) return node;

        if ( val < node.value ) {
            node.left = this.remove(val, node.left);
        }
        else if ( val > node.value ) {
            node.right = this.remove(val, node.right);
        }
        else {
            if ( node.left === null ) {
                return node.right;
            }
            else if ( node.right === null ) {
                return node.left;
            }
            else {
                node.value = this.inOrderSuccessor(node.right);
                node.right = this.remove(node.value, node.right);
            }
        }

        return node;
    }

    inOrderSuccessor(node) {
        if ( node === null ) {
            return node;
        }
        else if ( node.left !== null ) {
            return this.inOrderSuccessor(node.left);
        }
        else {
            return node.value;
        }
    }

    getScaleFactor() {
        let scale_factor = (window.innerWidth - 20) / ( Math.pow(2, this.height() - 1) * 70 );
        return 'scale(' + (scale_factor > 1 ? 1 : scale_factor) + ')';
    }

    receiveInput(val) {
        alert(val);
    }

    Tree(node = this.root, tree_height = this.height(), curr_height = 0, pos = 'root') {
        if (curr_height === tree_height) return null;

        const leftChild  = this.Tree(node ? node.left  : null, tree_height, curr_height + 1, 'left');
        const rightChild = this.Tree(node ? node.right : null, tree_height, curr_height + 1, 'right');
        
        const classes = ['node-wrapper', pos].join(' ');

        if ( node == null ) {
            return (
                <>
                    <div
                        className={classes}
                        node-value='null'
                        node-layer={tree_height - curr_height}>

                            <div className='node-value'>
                                {this.getCricle('null')}
                            </div>

                            {leftChild}
                            {rightChild}
                    </div>
                </>
            );
        }
        else {
            return (
                <>
                    <CSSTransition
                        in={this.state.animation1}
                        timeout={node.animations['timeout']}
                        appear={node.animations['appear']}
                        classNames={{
                            enterDone: 'visible',
                            exit: 'visible'
                        }}>

                        <div
                            className={classes}
                            node-value={node.value}
                            node-layer={tree_height - curr_height}
                            style={ ( node === this.root ) ? {width: Math.pow(2, tree_height - 1) * 70 + 'px'} : null }>

                                <div className='node-value'>
                                    {this.getNodeHover(node.value)}
                                    {this.getCricle(node.value)}
                                </div>

                                {leftChild}
                                {rightChild}

                                {this.getConnectingLine(node, pos)}

                        </div>

                    </CSSTransition>
                </>
            );
        }
    }

    getCricle(val) {
        return (
            <>
                <svg className='circle node-value-circle' xmlns='http://www.w3.org/2000/svg'>
                    <text className='text node-value-text' x='50%' y='50%' fill='black' textAnchor='middle' dominantBaseline='central'>
                        {val}
                    </text>
                    <circle className='circle node-circle' r='24' cx='25' cy='25' stroke='black' fill='none' />
                </svg>
            </>
        );
    }

    getNodeHover(val) {
        return (
            <>
                <span className='node-hover'>
                    <span className='search' onClick={() => this.searchForNode(val)}>
                        <i className="fas fa-search" />
                    </span>

                    <span className='trash' onClick={() => this.trashNode(val)}>
                        <i className="fas fa-trash" />
                    </span>
                </span>
            </>
        );
    }
    
    getConnectingLine(node, pos) {
        if (pos === 'left') {
            return (
                <>
                    <CSSTransition
                        in={this.state.animation1}
                        timeout={node.animations['timeout']}
                        appear={node.animations['appear']}
                        classNames={{
                            enterDone: 'visible',
                            exit: 'visible'
                        }}>

                        <svg className='line node-line' xmlns='http://www.w3.org/2000/svg'>
                            <line className='line' x1='0' y1='50' x2='100%' y2='0'></line>
                        </svg>

                    </CSSTransition>
                </>
            );
        }
        else if (pos === 'right') {
            return (
                <>
                    <CSSTransition
                        in={this.state.animation1}
                        timeout={node.animations['timeout']}
                        appear={node.animations['appear']}
                        classNames={{
                            enterDone: 'visible',
                            exit: 'visible'
                        }}>

                        <svg className='line node-line' xmlns='http://www.w3.org/2000/svg'>
                            <line className='line' x1='0' y1='0' x2='100%' y2='50'></line>
                        </svg>

                    </CSSTransition>
                </>
            );
        }
        else {
            return null;
        }
    }

    setDefaultAnimation(node = this.root) {
        if (node == null) return;
        node.animations['timeout'] = 0;
        node.animations['appear'] = false;

        this.setDefaultAnimation(node.left);
        this.setDefaultAnimation(node.right);
    }

    setGenerationAnimation() {
        let queue = new Queue();
        queue.push(this.root);

        let timeout = 200;

        while ( ! queue.empty() ) {
            let node = queue.front();

            if (node) {
                queue.push(node.left);
                queue.push(node.right);

                node.animations['timeout'] = timeout;
                node.animations['appear'] = true;
                
                timeout += 200;
            }
        }
