import React from 'react';
import logo from './logo.png'
import { forEach } from '../Helpers.js'
import '../node_modules/font-awesome/css/font-awesome.min.css'; 
import './NavigationBar.css';

export default class NavigationBar extends React.Component {
    constructor(props) {
        super(props);
        
        this.trees = {
            "BST" : "Binary Search Tree",
            "AVL" : "AVL Tree",
            "RBT" : "Red-Black Tree",
            "BHP" : "Binary Heap"
        };

        this.state = {
            active: "BST"
        };
    }

    openDropdown(selector) {
        document.getElementById(selector).getElementsByClassName('dropdown-menu')[0].classList.add('visible');
    }

    selectTree(tree_key) {
        this.setState(
            { active: tree_key }
        );

        forEach(
            document.getElementsByClassName('dropdown-menu'),
            function(x) {x.classList.remove('visible');}
        );

        this.props.visualizer.changeTree(tree_key);
    }

    render() {
        return (
            <>
                <div id="navbar-wrapper">
                    <span className="navbar-logo">
                        <img src={logo} alt="logo" />
                    </span>
                    
                    <span id="tree-dropdown" className="dropdown">
                        <a
                            className="dropdown-toggle"
                            href="#"
                            onClick={() => this.openDropdown("tree-dropdown")}>
                            {this.trees[ this.state.active ]}
                            <i className="fas fa-caret-down"></i>
                        </a>

                        <ul className="dropdown-menu">
                            {Object.keys(this.trees).map((key, idx) => (
                                <li className="dropdown-menu-option">
                                    <a
                                        href="#"
                                        onClick={() => this.selectTree(key)}>
                                        {this.trees[key]}
                                    </a>
                                </li>
                            ))}
                        </ul>
                    </span>
                </div>
            </>
        );
    }
}
