import React from 'react';
import NavigationBar from './NavigationBar/NavigationBar.jsx'
import BinarySearchTree from './Trees/BinarySearchTree/BinarySearchTree.jsx'
import './TreeVisualizer.css';


export default class TreeVisualizer extends React.Component {
    constructor(props) {
        super(props);
        
        this.state = {
            tree: new BinarySearchTree()         
        };
    }

    changeTree(tree_key) {
        switch(tree_key) {
            case "BST": this.setState( { tree: new BinarySearchTree() } );
                break;
            case "AVL":
            case "RBT":
            case "BHP":
            default:
                //show error, invalid tree
        }
    }

    generateNewTree() {
        this.state.tree.generateNewTree();
        this.forceUpdate();
    }

    insert(element) {
        this.state.tree.insert(element)
        this.forceUpdate();
    }

    remove(element) {
        this.forceUpdate();
    }

    find(element) {
        this.forceUpdate();
    }

    top() {

    }

    Tree() {
        return this.state.tree.paint();
    }

    render() {
        let scale_factor = window.innerWidth / ( Math.pow(2, this.state.tree.height() - 1) * 70 );
        scale_factor = scale_factor > 1 ? 1 : scale_factor;
        return (
            <>
                <NavigationBar visualizer = {this} />
                
                <div
                    id={ this.state.tree.name }
                    className="tree"
                    style={{transform: "scale(" + scale_factor + ")"}}>
                    { this.Tree() }
                </div>
            </>
        );
    }

    componentDidUpdate() {
        this.state.tree.animateNodes();
    }
}
