import React from 'react';
import Queue from '../../Queue.jsx'
import $ from 'jquery'
import { randomIntFromInverval } from '../../Helpers.js'
import { myMax } from '../../Helpers.js'
import './BinarySearchTree.css'

class BinarySearchTreeNode {
    constructor(val) {
        this.value = val;
        this.left  = null;
        this.right = null;
    }
}

export default class BinarySearchTree {
    constructor() {
        this.name = "BinarySearchTree";
        this.root = null;
    }

    generateNewTree(elements = 5) {
        this.root = null;
        $('.node-value').removeClass('visible');
        
        for (let i = 0; i < elements; ++i) {
            this.insert( randomIntFromInverval(1, 200) );
        }
    }

    height(node = this.root) {
        if (node == null) return 0;
        return myMax( this.height(node.left), this.height(node.right) ) + 1;
    }

    insert(val) {
        if (this.root == null) {
            this.root = new BinarySearchTreeNode(val);
            return true;
        }

        while (true) {
            if (val < curr.value) {
                if(curr.left == null) {
                    curr.left = new BinarySearchTreeNode(val);
                    break;
                }
                curr = curr.left;
            }
            else {
                if(curr.right == null) {
                    curr.right = new BinarySearchTreeNode(val);
                    break;
                }
                curr = curr.right;
            }
        }

        let height_cur = this.height();
    }

    animateNodes() {
        let queue = new Queue();
        queue.push( $('.node:not([node-value="null"])>.node-value:not(.visible)').first() );
        animateHelper(queue);
    }

    paint(node = this.root, tree_height = this.height(), curr_height = 0) {
        if (curr_height === tree_height) return null;

        const leftChild = this.paint(node ? node.left : null, tree_height, curr_height + 1);
        const rightChild = this.paint(node ? node.right : null, tree_height, curr_height + 1);
        
        if ( node == null ) {
            return (
                <>
                    <div className="node" node-value="null">
                        <div className="node-value">null</div>
                        {leftChild}
                        {rightChild}
                    </div>
                </>
            );
        }
        else if ( node === this.root ) {
            let scale_factor = window.innerWidth / ( Math.pow(2, tree_height - 1) * 70 );
            scale_factor = scale_factor > 1 ? 1 : scale_factor;

            return (
                <>
                    <div
                        className="node root"
                        node-value={node.value}
                        node-layer={tree_height - curr_height}
                        style={
                            {width: Math.pow(2, tree_height - 1) * 70 + 'px'},
                            {transform: "scale(" + scale_factor + ")"}
                            }
                    >
                            <div className="node-value">{node.value}</div>
                            {leftChild}
                            {rightChild}
                    </div>
                </>
            );
        }
        else {
            return (
                <>
                    <div
                        className="node"
                        node-value={node.value}
                        node-layer={tree_height - curr_height}
                    >
                            <div className="node-value">{node.value}</div>
                            {leftChild}
                            {rightChild}
                    </div>
                </>
            );
        }
    }
}

function animateHelper(queue) {
    if ( !queue.empty() ) {
        let $_node = queue.front();
        $_node.addClass('visible');
        $_node.siblings('.node:not([node-value="null"])').children('.node-value').each(function() {
           queue.push( $(this) ); 
        });
        setTimeout(function(){animateHelper(queue);}, 300);
    }
}
