import React from 'react';
import BinaryTree from './BinaryTree.jsx'
import './TreeVisualizer.css';


export default class TreeVisualizer extends React.Component {
    constructor(props) {
        super(props);
        
        this.state = {
            tree: new BinaryTree()           
        };

        this.state.tree.insert(1);
        this.state.tree.insert(3);
        this.state.tree.insert(5);
        this.state.tree.insert(7);
        //this.state.tree.insert( randomIntFromInverval(1, 3) );
    }

    render() {
        return this.state.tree.createDOM();
    }

    componentDidMount() {
        this.resetArray();
    }

    resetArray() {
        this.state.tree.resetTree();
    }
}

/*function randomIntFromInverval(min, max) {
    return Math.floor(Math.random() * (max - min + 1) + min);
}*/

function buildTreeInDOM(state) {
    /*const {array} = state;
    return (
        <>
            {array.map((value,idx) => (
                <div ClassName="array-bar" arr_key={idx}>
                    {value}
                </div> 
            ))}
        </>
    );*/

    return state.tree.createDOM();

    return (
        <>
            <div className="node">
                <div className="node-value">1</div>
                <div className="node">
                    <div className="node-value">4</div>
                </div>
                <div className="node">
                    <div className="node-value">5</div>
                </div>
            </div>
        </>
    );
}

