import React from 'react';
import randomInt from '../Helpers.jsx'
import Queue from '../Queue.jsx'

class BinaryNode {
    constructor(val) {
        this.value = val;
        this.left  = null;
        this.right = null;
    }
}

export default class BinaryTree {
    constructor() {
        this.root = null;
    }

    resetTree(elements = 3) {
        for (let i = 0; i < elements; ++i) {
            this.insert( randomInt(1, 20) );
        }
    }

    insert(val) {
        if (this.root == null) {
            this.root = new BinaryNode(val);
            return;
        }

        let curr = this.root;

        while (true) {
            if (val < curr.value) {
                if(curr.left == null) {
                    curr.left = new BinaryNode(val);
                    return;
                }
                curr = curr.left;
            }
            else {
                if(curr.right == null) {
                    curr.right = new BinaryNode(val);
                    return;
                }
                curr = curr.right;
            }
        }
    }

    createDOM(node = this.root, tree_height = this.height(), curr_height = 0) {
        if ( node == null ) {
            return (
                <>
                    <div className="node" node-value="null">
                        <div className="node-value">null</div>
                    </div>
                </>
            );
        }

        const leftChild = this.createDOM(node.left);
        const rightChild = this.createDOM(node.right);
        
        return (
            <>
                <div
                    className="node"
                    node-value={node.value}>
                        <div className="node-value">{node.value}</div>
                        {leftChild}
                        {rightChild}
                </div>
            </>
        );
    }
}
