import Queue from './Queue.jsx'

class BinaryNode {
    constructor(val) {
        this.value = val;
        this.left  = null;
        this.right = null;
    }
}

export default class BinaryTree {
    constructor() {
        this.root = null;
    }

    insert(val) {
        if (this.root == null) {
            this.root = new BinaryNode(val);
            return;
        }

        let curr = this.root;

        while (true) {
            if (val < curr.value) {
                if(curr.left == null) {
                    curr.left = new BinaryNode(val);
                    return;
                }
                curr = curr.left;
            }
            else {
                if(curr.right == null) {
                    curr.right = new BinaryNode(val);
                    return;
                }
                curr = curr.right;
            }
        }
    }

    levelOrder() {
        const array = [];
        let queue = new Queue();

        let layer_size = 1;
        let layer_curr = 0;
        let layer_temp = [];
        let layer_valid = false;

        queue.push(this.root);

        while( !queue.empty() ) {
            let curr = queue.front();
            layer_temp.push(curr);
            layer_curr += 1;

            if ( layer_size === layer_curr ) {
                if ( !layer_valid ) {
                    return array;
                }

                array.push(layer_temp);
                layer_size *= 2;
                layer_curr = 0;
                layer_temp = [];
                layer_valid = false;
            }

            if ( curr == null ) {
                queue.push(null);
                queue.push(null);
            }
            else {
                layer_valid = true;
                queue.push(curr.left);
                queue.push(curr.right);
            }
        }

        return array;
    }
}
