import React from 'react';
import logo from './logo.png'
import $ from 'jquery'
import '../../../node_modules/font-awesome/css/font-awesome.min.css';
import './NavigationBar.css';

export default class NavigationBar extends React.Component {
    constructor(props) {
        super(props);

        this.visualizer = this.props.visualizer;

        this.generateTree = this.generateTree.bind(this);
        this.resetTree    = this.resetTree.bind(this);
        this.insert       = this.insert.bind(this);
        this.remove       = this.remove.bind(this);
        this.search       = this.search.bind(this);
        this.top          = this.top.bind(this);
        
        this.trees = {
            BST : "Binary Search Tree",
            AVL : "AVL Tree",
            RBT : "Red-Black Tree",
            BHP : "Binary Heap"
        };

        this.operations = {
            BST : [
                {
                    name : "Insert",
                    callback : this.visualizer.insert()
                },
                {
                    name : "Remove",
                    callback : this.remove
                },
                {
                    name : "Search",
                    callback : this.search
                }
                    ],
            AVL : [
                {
                    name : "Insert",
                    callback : this.insert
                },
                {
                    name : "Remove",
                    callback : this.remove
                },
                {
                    name : "Search",
                    callback : this.search
                }
                    ],
            RBT : [
                {
                    name : "Insert",
                    callback : this.insert
                },
                {
                    name : "Remove",
                    callback : this.remove
                },
                {
                    name : "Search",
                    callback : this.search
                }
                    ],
            BHP : [
                {
                    name : "Insert",
                    callback : this.insert
                },
                {
                    name : "Top",
                    callback : this.top
                }
                    ]
        };

        this.state = {
            active_tree: "BST"
        };

        $(document).click(function(e) {
            if ( ! e.target.classList.contains('dropdown-toggle') ) {
                closeAllDropdowns();
            }
        });

    }

    changeTree(tree_key, visualizer) {
        this.setState(
            { active_tree: tree_key }
        );

        closeAllDropdowns();

        visualizer.changeTree(tree_key);
    }

    generateTree() {
        this.visualizer.generateTree();
    }

    resetTree() {
        this.visualizer.reset();
    }

    insert() {
        this.visualizer.insert();
    }

    remove() {

    }

    search() {

    }

    top() {

    }

    performOperation(operation, visualizer) {
        closeAllDropdowns();

        if ( this.operations[ this.state.active_tree ].includes(operation) ) {
            switch( operation ) {
                case "Insert":
                    visualizer.insert( this.askInput(operation) );
                    break;
                case "Remove":
                    visualizer.remove( this.askInput(operation) );
                    break;
                case "Find":
                    visualizer.find( this.askInput(operation) );
                    break;
                case "Top":
                    visualizer.top();
                    break;
            }
        }
        else {
            //state error.. force update?
        }
    }

    render() {
        return (
            <>
                <div id="navbar-wrapper">
                    <div className="navbar-logo">
                        <img src={logo} alt="logo" />
                    </div>
                    
                    <div id="tree-dropdown" className="dropdown">
                        <a
                            className="dropdown-toggle"
                            href="#"
                            onClick={() => openDropdown("tree-dropdown")}>
                            {this.trees[ this.state.active_tree ]}
                            <i className="fa fa-caret-down"></i>
                        </a>

                        <ul className="dropdown-menu">
                            {Object.keys(this.trees).map((key, idx) => (
                                <li className="dropdown-menu-option">
                                    <a
                                        href="#"
                                        onClick={(e) => this.changeTree(key, this.visualizer)}>
                                        {this.trees[key]}
                                    </a>
                                </li>
                            ))}
                        </ul>
                    </div>

                    <div id="operations-dropdown" className="dropdown">
                        <a
                            className="dropdown-toggle"
                            href="#"
                            onClick={() => openDropdown("operations-dropdown")}>
                            Operations
                            <i className="fa fa-caret-down"></i>
                        </a>

                        <ul className="dropdown-menu">
                            {this.operations[ this.state.active_tree ].map((operation, idx) => (
                                <li className="dropdown-menu-option">
                                    <a
                                        href="#"
                                        onClick={operation["callback"]}>
                                        {operation["name"]}
                                    </a>
                                </li>
                            ))}
                        </ul>
                    </div>

                    <div id="generate-tree" className="navbar-button">
                        <button
                            onClick={this.generateTree}>
                            Generate
                        </button>
                    </div>

                    <div id="reset-tree" className="navbar-button">
                        <button
                            onClick={this.resetTree}>
                            Reset
                        </button>
                    </div>
                </div>
            </>
        );
    }
}

function openDropdown(selector) {
    closeAllDropdowns();
    $('#' + selector).addClass('visible');
}

function closeAllDropdowns() {
    $('.dropdown').removeClass('visible');
}
