import React from 'react';

import NavigationBar from './NavigationBar/NavigationBar.jsx'
import Tooltips from './Tooltips/Tooltips.jsx'

import DefaultTree from './Trees/DefaultTree/DefaultTree.jsx'
import BinarySearchTree from './Trees/BinarySearchTree/BinarySearchTree.jsx'

import './TreeVisualizer.scss';


export default class TreeVisualizer extends React.Component {
    constructor(props) {
        super(props);
        
        this.state = {
            tree: new BinarySearchTree(),
            tree_state: 'Initial'
        };

        this.performOperation = this.performOperation.bind(this);
        this.toggleTooltips   = this.toggleTooltips.bind(this);
        this.changeTree       = this.changeTree.bind(this);
    }

    performOperation(operation) {
        const new_state = this.state.tree.performOperation(operation);
        this.setState( { tree_state: new_state } );
    }

    toggleTooltips() {
        // to-do
    }

    changeTree(tree_key) {
        switch (tree_key) {
            case 'BST': this.setState( { tree: new BinarySearchTree() } );
                break;
            case 'AVL': this.setState( { tree: new DefaultTree() } );
                break;
            case 'RBT': this.setState( { tree: new DefaultTree() } );
                break;
            case 'BHP': this.setState( { tree: new DefaultTree() } );
                break;
            default: this.setState( { tree: new DefaultTree() } );  //show error?, invalid tree
                break;
        }
    }

    Tree() {
        return (
            <>
                <div
                    id={ this.state.tree.name }
                    className='tree'
                    style={ {transform: this.state.tree.getScaleFactor()} }>

                    { this.state.tree.Tree() }

                </div>
            </>
        );
    }

    render() {
        return (
            <>
                <NavigationBar visualizer = {this} />
                
                { this.Tree() }

                <Tooltips active = {false} />
            </>
        );
    }
}
