import React from 'react';
import $ from 'jquery'

import DefaultTree from '../DefaultTree/DefaultTree.jsx'
import Queue from '../../Additional/Queue.js'

import { randomIntFromInverval } from '../../Additional/Helpers.js'

import './BinarySearchTree.scss'

class BinarySearchTreeNode {
    constructor(val) {
        this.value = val;
        this.left  = null;
        this.right = null;
    }
}

export default class BinarySearchTree extends DefaultTree {
    constructor() {
        super();

        this.name = 'BinarySearchTree';
        this.key  = 'BST';
        this.root = null;
    }

     performOperation(operation) {
        switch ( operation ) {
            case 'Generate':
                this.generate();
                break;
            default:
                break;
        }
    }

    preRender(operation) {
        switch ( operation ) {
            case 'Generate':
                hideNodes();
                break;
            default:
                break;
        }
    }

    postRender(operation) {
        switch ( operation ) {
            case 'Generate':
                showNodes();
                break;
            default:
                break;
        }
    }

    generate(elements = 5) {
        this.root = null;
        
        for (let i = 0; i < elements; ++i) {
            this.insert( randomIntFromInverval(1, 200) );
        }
    }

    reset() {
        this.root = null;
    }

    height(node = this.root) {
        if (node == null) return 0;
        return Math.max( this.height(node.left), this.height(node.right) ) + 1;
    }

    insert(val) {
        if (this.root == null) {
            this.root = new BinarySearchTreeNode(val);
            return true;
        }
        
        let curr = this.root;

        while (true) {
            if (curr.value < val) {
                if(curr.right == null) {
                    curr.right = new BinarySearchTreeNode(val);
                    break;
                }
                curr = curr.right;
            }
            else {
                if(curr.left == null) {
                    curr.left = new BinarySearchTreeNode(val);
                    break;
                }
                curr = curr.left;
            }
        }
    }

    getScaleFactor() {
        let scale_factor = (window.innerWidth - 20) / ( Math.pow(2, this.height() - 1) * 70 );
        return 'scale(' + (scale_factor > 1 ? 1 : scale_factor) + ')';
    }

    Tree(node = this.root, tree_height = this.height(), curr_height = 0, pos = 'root') {
        if (curr_height === tree_height) return null;

        const leftChild  = this.Tree(node ? node.left  : null, tree_height, curr_height + 1, 'left' );
        const rightChild = this.Tree(node ? node.right : null, tree_height, curr_height + 1, 'right');
        
        let classes = ['node-wrapper', pos].join(' ');

        if ( node == null ) {
            return (
                <>
                    <div
                        className={classes}
                        node-value='null'
                        node-layer={tree_height - curr_height}>

                            <div className='node-value'>
                                {getCricle('null')}
                            </div>

                            {leftChild}
                            {rightChild}
                    </div>
                </>
            );
        }
        else if ( node === this.root ) {
            return (
                <>
                    <div
                        className={classes}
                        node-value={node.value}
                        node-layer={tree_height - curr_height}
                        style={ {width: Math.pow(2, tree_height - 1) * 70 + 'px'} }>

                            <div className='node-value'>
                                <span className='node-hover'>HOVER</span>
                                {getCricle(node.value)}
                            </div>

                            {leftChild}
                            {rightChild}

                    </div>
                </>
            );
        }
        else {
            return (
                <>
                    <div
                        className={classes}
                        node-value={node.value}
                        node-layer={tree_height - curr_height}>

                            <div className='node-value'>
                                <span className='node-hover'>HOVER</span>
                                {getCricle(node.value)}
                            </div>
                            
                            {leftChild}
                            {rightChild}
                            {getConnectingLine(pos)}
                            
                    </div>
                </>
            );
        }
    }
}

function getCricle(val) {
    return (
        <>
            <svg className='circle node-value-circle' xmlns='http://www.w3.org/2000/svg'>
                <text className='text node-value-text' x='50%' y='50%' fill='black' textAnchor='middle' dominantBaseline='central'>
                    {val}
                </text>
                <circle className='circle node-circle' r='24' cx='25' cy='25' stroke='black' fill='none' />
            </svg>
        </>
    );
}

function getConnectingLine(pos) {
    if (pos === 'left') {
        return (
            <>
                <svg className='line node-line' xmlns='http://www.w3.org/2000/svg'>
                    <line className='line' x1='0' y1='50' x2='100%' y2='0'></line>
                </svg>
            </>
        );
    }
    else if (pos === 'right') {
        return (
            <>
                <svg className='line node-line' xmlns='http://www.w3.org/2000/svg'>
                    <line className='line' x1='0' y1='0' x2='100%' y2='50'></line>
                </svg>
            </>
        );
    }
    else {
        return null;
    }
}

function hideNodes() {
    $('.node-wrapper').removeClass('visible');
}

function showNodes() {
    let queue = new Queue();
    queue.push( $('.node-wrapper:not([node-value="null"]):not(.visible)').first() );
    setTimeout(function(){animateHelper(queue);}, 300);
}

function animateHelper(queue) {
    if ( !queue.empty() ) {
        let $_node = queue.front();
        $_node.addClass('visible');
        $_node.children('.node-wrapper:not([node-value="null"])').each(function() {
           queue.push( $(this) ); 
        });
        setTimeout(function(){animateHelper(queue);}, 300);
    }
}
