import React from 'react';
import anime from 'animejs'
import { CSSTransition } from 'react-transition-group'

import InputModal from '../../Additional/InputModal/InputModal.jsx'
import ErrorMessage from '../../Additional/ErrorMessage/ErrorMessage.jsx'

import Queue from '../../Additional/Queue.js'
import { randomIntFromInverval } from '../../Additional/Helpers.js'

import './BinarySearchTree.scss'

class BinarySearchTreeNode {
    constructor(val) {
        this.value = val;
        this.left  = null;
        this.right = null;

        this.animations = {
            initial: {
                node: {
                    active: false, delay: 0
                },
                line: {
                    active: false,  delay: 0
                }
            },
            visited: {
                node: {
                    active: false, delay: 0
                },
                line: {
                    active: false,  delay: 0
                }
            },
            success: {
                node: {
                    active: false, delay: 0
                },
                line: {
                    active: false,  delay: 0
                }
            },
            failed: {
                node: {
                    active: false, delay: 0
                },
                line: {
                    active: false,  delay: 0
                }
            }
        };
    }
}

export default class BinarySearchTree extends React.Component {
    constructor(props) {
        super(props);

        this.initialStateOperation = 'Initial';

        this.visibleClasses = {
            appearDone: 'visible',
            enterDone: 'visible',
            exit: 'visible'
        };

        this.name = 'BinarySearchTree';
        this.key  = 'BST';
        this.root = null;

        this.target = 0;
        this.success = false;

        this.state = {
            initial: false,
            visited: false,
            completed: false,
            animation4: false,
        };

        this.receiveInput = this.receiveInput.bind(this);

        this.action1 = this.action1.bind(this); // temporary
        this.action2 = this.action2.bind(this); // temporary
        this.action3 = this.action3.bind(this); // temporary
        this.action4 = this.action4.bind(this); // temporary
        this.action5 = this.action5.bind(this); // temporary
        this.action6 = this.action6.bind(this); // temporary
        this.action7 = this.action7.bind(this); // temporary
        this.action8 = this.action8.bind(this); // temporary
        this.action9 = this.action9.bind(this); // temporary
        this.action10 = this.action10.bind(this); // temporary
        this.action11 = this.action11.bind(this); // temporary
        this.action12 = this.action12.bind(this); // temporary
        this.action13 = this.action13.bind(this); // temporary
        this.action14 = this.action14.bind(this); // temporary
    }

    componentDidMount() {
        // call generate function
    }

    /*static getDerivedStateFromProps(newProps, state) {
        //To-do: async calls and tooltips
        console.log("BST::getDerivedStateFromProps::newProps "  + newProps.operation);
        console.log("BST::getDerivedStateFromProps::state.operation " + state.operation);
        console.log("BST::getDerivedStateFromProps::state.animating " + state.animating);

        const initialStateOperation = 'Initial';

        if ( state.animating ) {
            if ( state.operation === initialStateOperation ) {
                return { operation: state.operation, animating: false };
            }
            else {
                return { operation: state.operation };
            }
        }
        else if ( state.operation !== newProps.operation ) {
            return { operation: newProps.operation, animating: true };
        }
        else {
            return null;
        }
    }*/

    insertNumElements(elements) {
        for (let i = 0; i < elements; ++i) {
            let val = randomIntFromInverval(1, 150);
            if ( this.insert( val ) ) {
                if ( this.height() > 5 ) {
                    this.root = this.remove( val );
                    --i;
                }
            }
            else {
                --i;
            }
        }
    }

    reset() {
        this.root = null;
    }

    height(node = this.root) {
        if (node === null) return 0;
        return Math.max( this.height(node.left), this.height(node.right) ) + 1;
    }

    insert(val) {
        if (this.root === null) {
            this.root = new BinarySearchTreeNode(val);
            return true;
        }
        
        let curr = this.root;

        while (true) {
            if (curr.value === val) {
                //tree contains key already
                return false;
            }
            else if (curr.value < val) {
                if(curr.right === null) {
                    curr.right = new BinarySearchTreeNode(val);
                    return true;
                }
                curr = curr.right;
            }
            else {
                if(curr.left === null) {
                    curr.left = new BinarySearchTreeNode(val);
                    return true;
                }
                curr = curr.left;
            }
        }
    }

    remove(val, node = this.root) {
        if ( node === null ) return node;

        if ( val < node.value ) {
            node.left = this.remove(val, node.left);
        }
        else if ( val > node.value ) {
            node.right = this.remove(val, node.right);
        }
        else {
            if ( node.left === null ) {
                return node.right;
            }
            else if ( node.right === null ) {
                return node.left;
            }
            else {
                node.value = this.inOrderSuccessor(node.right);
                node.right = this.remove(node.value, node.right);
            }
        }

        return node;
    }

    inOrderSuccessor(node) {
        if ( node === null ) {
            return node;
        }
        else if ( node.left !== null ) {
            return this.inOrderSuccessor(node.left);
        }
        else {
            return node.value;
        }
    }

    getScaleFactor() {
        let scale_factor = (window.innerWidth - 20) / ( Math.pow(2, this.height() - 1) * 70 );
        return 'scale(' + (scale_factor > 1 ? 1 : scale_factor) + ')';
    }

    receiveInput(val) {
        alert(val);
    }

    searchForNode(val) {
        this.action5(val);
    }

    trashNode(val) {
        alert(val);
    }

    Tree(node = this.root, tree_height = this.height(), curr_height = 0, pos = 'root') {
        if (curr_height === tree_height) return null;

        const leftChild  = this.Tree(node ? node.left  : null, tree_height, curr_height + 1, 'left');
        const rightChild = this.Tree(node ? node.right : null, tree_height, curr_height + 1, 'right');
        
        const classes = ['node-wrapper', pos].join(' ');

        if ( node === null ) {
            return (
                <>
                    <div
                        className={classes}
                        node-value='null'
                        node-layer={tree_height - curr_height}>

                            <div className='node-value' />

                            {leftChild}
                            {rightChild}
                    </div>
                </>
            );
        }
        else {
            return (
                <>
                    <CSSTransition
                        in={node.animations['initial']['node']['active']}
                        timeout={node.animations['initial']['node']['delay']}
                        classNames={this.visibleClasses}
                        appear={true}>
                        
                        <div
                            className={classes}
                            node-value={node.value}
                            node-layer={tree_height - curr_height}
                            style={ ( node === this.root ) ? {width: Math.pow(2, tree_height - 1) * 70 + 'px'} : null }>

                            <div className='node-value'>
                                {this.getNodeHover(node.value)}
                                {this.getCricle(node)}
                            </div>

                            {leftChild}
                            {rightChild}

                            {this.getConnectingLine(node, pos)}

                        </div>

                    </CSSTransition>
                </>
            );
        }
    }

    getCricle(node) {
        return (
            <>
                <svg className='circle node-value-circle' xmlns='http://www.w3.org/2000/svg'>

                    <circle className='circle node-circle initial' r='24' cx='25' cy='25' fill='none' />

                    <CSSTransition
                        in={node.animations['visited']['node']['active']}
                        timeout={node.animations['visited']['node']['delay']}
                        classNames={this.visibleClasses}
                        appear={true}>

                        <svg className='circle node-circle-traversal' xmlns='http://www.w3.org/2000/svg'>
                            <circle className='circle node-circle-background visited' r='24' cx='25' cy='25' fill='none' />
                            <circle className='circle node-circle visited' r='24' cx='25' cy='25' fill='none' />
                        </svg>

                    </CSSTransition>

                    <CSSTransition
                        in={node.animations['success']['node']['active']}
                        timeout={node.animations['success']['node']['delay']}
                        classNames={this.visibleClasses}
                        appear={true}>

                        <svg className='circle node-circle-traversal' xmlns='http://www.w3.org/2000/svg'>
                            <circle className='circle node-circle-background success' r='24' cx='25' cy='25' fill='none' />
                            <circle className='circle node-circle success' r='24' cx='25' cy='25' fill='none' />
                        </svg>

                    </CSSTransition>
                
                    <CSSTransition
                        in={node.animations['failed']['node']['active']}
                        timeout={node.animations['failed']['node']['delay']}
                        classNames={this.visibleClasses}
                        appear={true}>

                        <svg className='circle node-circle-traversal' xmlns='http://www.w3.org/2000/svg'>
                            <circle className='circle node-circle-background failed' r='24' cx='25' cy='25' fill='none' />
                            <circle className='circle node-circle failed' r='24' cx='25' cy='25' fill='none' />
                        </svg>

                    </CSSTransition>
                    
                    <text className='text node-value-text' x='50%' y='50%' fill='black' textAnchor='middle' dominantBaseline='central'>
                        {node.value}
                    </text>

                </svg>
            </>
        );
    }

    getNodeHover(val) {
        return (
            <>
                <span className='node-hover'>
                    <i className="fas fa-search" onClick={() => this.searchForNode(val)} />
                    <span className='search'>
                        <svg className='circle' xmlns='http://www.w3.org/2000/svg'>
                            <circle className='circle node-hover-circle' r='24' cx='25' cy='25' fill='none' />
                        </svg>
                    </span>

                    <i className="fas fa-trash" onClick={() => this.trashNode(val)} />
                    <span className='trash'>
                        <svg className='circle' xmlns='http://www.w3.org/2000/svg'>
                            <circle className='circle node-hover-circle' r='24' cx='25' cy='25' fill='none' />
                        </svg>
                    </span>
                </span>
            </>
        );
    }
    
    getConnectingLine(node, pos) {
        if (pos === 'left' || pos === 'right') {
            return (
                <>
                    <CSSTransition
                        in={node.animations['initial']['line']['active']}
                        timeout={node.animations['initial']['line']['delay']}
                        classNames={this.visibleClasses}
                        appear={true}>

                        <svg className='line node-line' xmlns='http://www.w3.org/2000/svg'>

                            {this.getLine(pos, 'initial')}

                            <CSSTransition
                                in={node.animations['visited']['line']['active']}
                                timeout={node.animations['visited']['line']['delay']}
                                classNames={this.visibleClasses}
                                appear={true}>

                                {this.getLine(pos, 'visited')}

                            </CSSTransition>

                            <CSSTransition
                                in={node.animations['success']['line']['active']}
                                timeout={node.animations['success']['line']['delay']}
                                classNames={this.visibleClasses}
                                appear={true}>

                                {this.getLine(pos, 'success')}

                            </CSSTransition>

                            <CSSTransition
                                in={node.animations['failed']['line']['active']}
                                timeout={node.animations['failed']['line']['delay']}
                                classNames={this.visibleClasses}
                                appear={true}>

                                {this.getLine(pos, 'failed')}

                            </CSSTransition>

                        </svg>

                    </CSSTransition>
                </>
            );
        }
        else {
            return null;
        }
    }

    getLine(pos, addClass) {
        const classes = ['line', addClass].join(' ');

        if (pos === 'left')
            return ( <line className={classes} x1='0' y1='50' x2='100%' y2='0' /> );
        else if (pos === 'right')
            return ( <line className={classes} x1='0' y1='0' x2='100%' y2='50' /> );
        else
            return null;
    }

    setGenerationAnimations() {
        let queue = new Queue();
        queue.push(this.root);

        let delay = 150;

        while ( ! queue.empty() ) {
            let node = queue.front();

            if (node) {
                queue.push(node.left);
                queue.push(node.right);

                for (let key in node.animations['initial']) {
                    node.animations['initial'][key]['active'] = true;
                    node.animations['initial'][key]['delay'] = delay;
                }
                
                delay += 200;
            }
        }
    }

    setHideAnimations(node = this.root) {
        if (node === null) return;

        for (let state in node.animations) {
            for (let element in node.animations[state]) {
                node.animations[state][element]['active'] = false;
                node.animations[state][element]['delay'] = 150;
            }
        }

        this.setHideAnimations(node.left);
        this.setHideAnimations(node.right);
    }

    setHideTraversalAnimations(node = this.root) {
        if (node === null) return;

        for (let state in node.animations) {
            for (let element in node.animations[state]) {
                if (state === 'initial') {
                    node.animations[state][element]['active'] = true;
                }
                else {
                    node.animations[state][element]['active'] = false;
                }

                node.animations[state][element]['delay'] = 150;
            }
        }

        this.setHideTraversalAnimations(node.left);
        this.setHideTraversalAnimations(node.right);
    }

    setDFSTraversalAnimations(order, node = this.root, delay = 150) {
        if (node === null) return delay;

        let _delay = node === this.root ? delay : delay + 1000;

        node.animations['visited']['node']['active'] = true;
        node.animations['visited']['node']['delay'] = _delay + 500;
        node.animations['visited']['line']['active'] = true;
        node.animations['visited']['line']['delay'] = _delay;

        if (order === 'Pre-order') {
            _delay += 1000;
            node.animations['success']['node']['active'] = true;
            node.animations['success']['node']['delay'] = _delay;
        }

        _delay = this.setDFSTraversalAnimations(order, node.left, _delay);

        if (order === 'In-order') {
            _delay += 1000;
            node.animations['success']['node']['active'] = true;
            node.animations['success']['node']['delay'] = _delay;
        }

        _delay = this.setDFSTraversalAnimations(order, node.right, _delay);

        if (order === 'Post-order') {
            _delay += 1000;
            node.animations['success']['node']['active'] = true;
            node.animations['success']['node']['delay'] = _delay;
        }

        node.animations['success']['line']['active'] = true;
        node.animations['success']['line']['delay'] = _delay;

        return _delay;
    }

    setBFSTraversalAnimations() {
        let queue = new Queue();

        let delay = 150;

        if (this.root) {
            queue.push(this.root);

            this.root.animations['visited']['node']['active'] = true;
            this.root.animations['visited']['node']['delay'] = delay;
        }
        else {
            //error..tree is empty
        }

        while ( ! queue.empty() ) {
            let node = queue.front();

            if (node) {
                if (node.left) {
                    queue.push(node.left);

                    delay += 1000;
                    for (let element in node.left.animations['visited']) {
                        node.left.animations['visited'][element]['active'] = true;
                        node.left.animations['visited'][element]['delay'] = delay;
                    }
                }

                if (node.right) {
                    queue.push(node.right);

                    delay += 1000;
                    for (let element in node.right.animations['visited']) {
                        node.right.animations['visited'][element]['active'] = true;
                        node.right.animations['visited'][element]['delay'] = delay;
                    }
                }
                
                delay += 1000;
                for (let element in node.animations['success']) {
                    node.animations['success'][element]['active'] = true;
                    node.animations['success'][element]['delay'] = delay;
                }
            }
        }
    }

    setSearchAnimations(node = this.root, delay = 150) {
        if ( ! this.target ) return;

        if (node === null) {
            if (node === this.root) {
                //show error..empty tree
            }
        }
        else {
            if (this.target === node.value)
                this.success = true;
            else if (this.target < node.value)
                this.setSearchAnimations(node.left, delay + 1000);
            else
                this.setSearchAnimations(node.right, delay + 1000);
                
            node.animations['visited']['node']['active'] = true;
            node.animations['visited']['node']['delay'] = delay + 500;
            node.animations['visited']['line']['active'] = true;
            node.animations['visited']['line']['delay'] = delay;
        }
    }

    setSearchCompleteAnimations(node = this.root, success = this.success ? 'success' : 'failed') {
        if ( ! this.target ) return;

        if (node === null) {
            if (node === this.root) {
                //show error..empty tree
            }
        }
        else {
            if (this.target === node.value)
                this.success = false;
            else if (this.target < node.value)
                this.setSearchCompleteAnimations(node.left, success);
            else
                this.setSearchCompleteAnimations(node.right, success);
                
            node.animations[success]['node']['active'] = true;
            node.animations[success]['node']['delay'] = 300;
            node.animations[success]['line']['active'] = true;
            node.animations[success]['line']['delay'] = 300;
        }
    }

    setInsertAnimations(node = this.root, delay = 150) {
        if ( ! this.target || ! node ) return;
        else {
            if (this.target === node.value)
                return;
            else if (this.target < node.value)
                this.setInsertAnimations(node.left, delay + 1000);
            else
                this.setInsertAnimations(node.right, delay + 1000);
                
            node.animations['visited']['node']['active'] = true;
            node.animations['visited']['node']['delay'] = delay + 500;
            node.animations['visited']['line']['active'] = true;
            node.animations['visited']['line']['delay'] = delay;
        }
    }

    setInsertionCompleteAnimations(node = this.root) {
        if ( ! this.target || ! node ) return;
        else {
            if (this.target === node.value) {
                node.animations['initial']['node']['active'] = true;
                node.animations['initial']['node']['delay'] = 650;
                node.animations['initial']['line']['active'] = true;
                node.animations['initial']['line']['delay'] = 150;
            }
            else if (this.target < node.value)
                this.setInsertionCompleteAnimations(node.left);
            else
                this.setInsertionCompleteAnimations(node.right);
                
            node.animations['success']['node']['active'] = true;
            node.animations['success']['node']['delay'] = 1000;
            node.animations['success']['line']['active'] = true;
            node.animations['success']['line']['delay'] = 1000;
        }
    }

    action1() {
        this.reset();
        this.insertNumElements(15);
        this.setGenerationAnimations();
        this.setState({initial: true});
    }

    action2() {
        this.target = null;
        this.success = false;
        this.setHideAnimations();
        this.setState({initial: true});
    }

    action3() {
        this.target = null;
        this.success = false;
        this.setHideTraversalAnimations();
        this.setState({initial: true});
    }

    action4() {
        //this.setDFSTraversalAnimations('In-order');
        this.setBFSTraversalAnimations();
        this.setState({initial: true});
    }

    action5(val = null) {
        this.target = val;
        this.setSearchAnimations();
        this.setState({initial: true});
    }

    action6() {
        this.setSearchCompleteAnimations();
        this.setState({initial: true});
    }

    action7() {
        this.target = parseInt( prompt() );
        this.insert(this.target);
        this.setInsertAnimations();
        this.setState({initial: true});
    }

    action8() {
        this.setInsertionCompleteAnimations();
        this.setState({initial: true});
    }

    action9() {
    }

    action10() {
    }

    action11() {
    }

    action12() {
    }

    action13() {
    }

    action14() {
        this.reset();
    }

    render() {
        console.log("BST::render: ");
        console.log(this.state);
        return (
            <>
                <button onClick = {this.action1}>Generate new tree</button>
                <button onClick = {this.action2}>Hide Tree</button>
                <button onClick = {this.action3}>Hide traversals</button>
                <button onClick = {this.action4}>Traversals</button>
                <button onClick = {this.action5}>Search</button>
                <button onClick = {this.action6}>Search complete</button>
                <button onClick = {this.action7}>Insert</button>
                <button onClick = {this.action8}>Insert complete</button>
