import React from 'react';
import BinaryTree from './Trees/BinaryTree.jsx'
import './TreeVisualizer.css';


export default class TreeVisualizer extends React.Component {
    constructor(props) {
        super(props);
        
        this.state = {
            tree: new BinaryTree()           
        };

        this.reset();
    }

    render() {
        return this.state.tree.createDOM();
    }

    componentDidMount() {
    }

    resetTree() {
        this.state.tree.resetTree();
    }
}

