import React from 'react'
import{ CSSTransition } from 'react-transition-group'
import isEqual from 'react-fast-compare'

import './Tooltips.scss';

export default class Tooltips extends React.Component {
    constructor(props) {
        super(props);

        this.visibleClasses = {
            appearDone: 'visible',
            enterDone: 'visible',
            exit: 'visible'
        };

        this.hiddenClasses = {
            appearActive: 'hiding',
            enterActive: 'hiding',
            appearDone: 'hidden',
            enterDone: 'hidden'
        };

        this.operationSteps = this.props.operationSteps;
        this.next = this.props.next;

        this.state = {
            active: this.props.active,
            timeout: this.props.timeout,
            operations: {
                current: {
                    operation: this.props.operation,
                    step: this.props.step
                },
                previous: {
                    operation: this.props.operation,
                    step: this.props.step
                }
            }
        };
    }

    static getDerivedStateFromProps(newProps, state) {
        if (
            state.operations.current.operation !== newProps.operation ||
            state.operations.current.step !== newProps.step ||
            state.active !== newProps.active
        ) {
            return {
                active: newProps.active,
                timeout: newProps.timeout,
                operations: {
                    current: {
                        operation: newProps.operation,
                        step: newProps.step
                    },
                    previous: state.operations.current
                }
            };
        }
        else {
            return null;
        }
    }

    getContent() {
        return null;
    }

    getPreviousTooltips() {
        return (
            <>
                <CSSTransition
                    in={ isEqual(this.state.operations.current, this.state.operations.previous) }
                    timeout={500}
                    classNames={this.hiddenClasses}
                    appear={true}
                    key={this.state.operations.current.operation + this.state.operations.current.step}>

                    <div className='prev-tooltips'>
                        { this.getContent(this.state.operations.previous) }
                    </div>

                </CSSTransition>
            </>
        );
    }

    getCurrentTooltips() {
        return (
            <>
                <CSSTransition
                    in={ isEqual(this.state.operations.current, this.state.operations.previous) }
                    timeout={500}
                    classNames={this.visibleClasses}
                    appear={true}
                    key={this.state.operations.current.operation + this.state.operations.current.step}>

                    <div className='curr-tooltips'>
                        { this.getContent(this.state.operations.current) }
                    </div>

                </CSSTransition>
            </>
        );
    }

    getNextButton() {
        return (
            <>
                <CSSTransition
                    in={this.state.active}
                    timeout={this.state.timeout}
                    classNames={this.visibleClasses}
                    appear={true}
                    key={this.state.operations.current.operation + this.state.operations.current.step}>

                    <a
                        className='tooltips-next'
                        href='# '
                        onClick={this.next}>
                        Next step
                        <i className="fas fa-arrow-right" />
                        <div className='next-underline' />
                    </a>

                </CSSTransition>
            </>
        );
    }

    render() {
        console.log(this.state);
        console.log(  isEqual(this.state.operations.current, this.state.operations.previous)  );
        if ( this.state.active ) {
            return (
                <>
                    <div id='tooltips'>
                        { this.getPreviousTooltips() }
                        { this.getCurrentTooltips() }
                        { this.getNextButton() }
                    </div>
                </>
            );
        }
        else {
            return null;
        }
    }
}
