import React from 'react';
import logo from './logo.png'
import $ from 'jquery'
import '../../../node_modules/font-awesome/css/font-awesome.min.css';
import './NavigationBar.css';

export default class NavigationBar extends React.Component {
    constructor(props) {
        super(props);

        this.visualizer     = this.props.visualizer;
        
        this.trees = {
            BST : {
                name : "Binary Search Tree",
                operations : [
                    "Insert",
                    "Remove",
                    "Search",
                    "Traversals"
                ]
            },
            AVL : {
                name : "AVL Tree",
                operations : [
                    "Insert",
                    "Remove",
                    "Search"
                ]
            },
            RBT : {
                name : "Red-Black Tree",
                operations : [
                    "Insert",
                    "Remove",
                    "Search"
                ]
            },
            BHP : {
                name : "Binary Heap",
                operations : [
                    "Insert",
                    "Top"
                ]
            },
        };

        this.traversals = [
            { name: "Pre-Order",   callback: "PreOrder" },
            { name: "In-Order",    callback: "InOrder" },
            { name: "Post-Order",  callback: "PostOrder" },
            { name: "Level Order", callback: "LevelOrder" }
        ];

        this.operations = {
            Insert     : this.visualizer.insert,
            Remove     : this.visualizer.remove,
            Search     : this.visualizer.search,
            Top        : this.visualizer.top,
            PreOrder   : this.visualizer.preOrder,
            InOrder    : this.visualizer.inOrder,
            PostOrder  : this.visualizer.postOrder,
            LevelOrder : this.visualizer.levelOrder
        };

        this.state = {
            active_tree: this.visualizer.state.tree.key
        };

        $(document).click(function(e) {
            if ( ! e.target.classList.contains('dropdown-toggle') ) {
                closeAllDropdowns();
            }
        });
    }

    changeTree(tree_key) {
        this.setState( { active_tree: tree_key } );
        this.visualizer.changeTree(tree_key);
    }

    static getDerivedStateFromProps(newProps, prevState) {
        return {active_tree: newProps.visualizer.state.tree.key};
    }

    render() {
        const treeActive = this.state.active_tree;

        return (
            <>
                <div id="navbar-wrapper">

                    <div className="navbar-logo">
                        <img src={logo} alt="logo" />
                    </div>
                    
                    <div id="tree-dropdown" className="dropdown">
                        <a
                            className="dropdown-toggle"
                            href="# "
                            onClick={() => openDropdown("tree-dropdown")}>
                            { treeActive ? this.trees[ this.state.active_tree ][ "name" ] : "Select.." }
                            <i className="fa fa-caret-down"></i>
                        </a>

                        <ul className="dropdown-menu">
                            {Object.keys(this.trees).map((key, idx) => (
                                <li className="dropdown-menu-option" key={idx}>
                                    <a
                                        href="# "
                                        onClick={() => this.visualizer.changeTree(key)}>
                                        {this.trees[ key ][ "name" ]}
                                    </a>
                                </li>
                            ))}
                        </ul>
                    </div>
                    
                    { treeActive ? (
                        <>
                            <div id="operations-dropdown" className="dropdown">
                                <a
                                    className="dropdown-toggle"
                                    href="# "
                                    onClick={() => openDropdown("operations-dropdown")}>
                                    Operations
                                    <i className="fa fa-caret-down"></i>
                                </a>

                                <ul className="dropdown-menu">
                                    {this.trees[ this.state.active_tree ][ "operations" ].map((operation, idx) => (

                                        operation === "Traversals" ? (
                                            <>
                                                <li className="dropdown-menu-option" key={idx}>
                                                    <a
                                                        href="# "
                                                        onClick={this.operations[ operation ]}>
                                                        {operation}
                                                    </a>
                                                </li>
                                            </>
                                        ) : (
                                            <>
                                                <li className="dropdown-menu-option" key={idx}>
                                                    <a
                                                        href="# "
                                                        onClick={() => openDropdown("traversals-submenu")}>
                                                        {operation}
                                                    </a>
                                                </li>
                                            </>
                                        )
                                    ))}
                                </ul>
                            </div>
                        
                            <div id="generate-tree" className="navbar-button">
                                <button
                                    onClick={this.visualizer.generateTree}>
                                    Generate
                                </button>
                            </div>

                            <div id="reset-tree" className="navbar-button">
                                <button
                                    onClick={this.visualizer.resetTree}>
                                    Reset
                                </button>
                            </div>

                            <div id="tooltips-toggle">
                                <span className="tooltips-text">Tooltips:</span>
                                <label className="switch">
                                    <input type="checkbox" onClick={this.visualizer.toggleTooltips} />
                                    <span className="slider round" />
                                </label>
                            </div>
                        </>
                    ) : ( null ) }

                </div>
            </>
        );
    }
}

function openDropdown(selector) {
    if ( $('#' + selector).hasClass('visible') ) {
        closeAllDropdowns();
        return;
    }
    else {
        closeAllDropdowns();
        $('#' + selector).addClass('visible');
    }
}

function closeAllDropdowns() {
    $('.dropdown.visible').removeClass('visible');
}
