import React from 'react';
import { CSSTransition } from 'react-transition-group'

import InputPrompt from '../../Additional/InputPrompt/InputPrompt.jsx'

import { randomIntFromInverval } from '../../Additional/Helpers.js'

import './BinarySearchTree.scss'

class BinarySearchTreeNode {
    constructor(val) {
        this.value = val;
        this.left  = null;
        this.right = null;
    }
}

export default class BinarySearchTree extends React.Component {
    constructor(props) {
        super(props);

        this.name = 'BinarySearchTree';
        this.key  = 'BST';
        this.root = null;

        this.state = {
            operation: this.props.operation,
            input: true
        };
    }

    componentDidMount() {
        this.generate();
        this.setState({ operation: 'Generate' });
    }

    static getDerivedStateFromProps(newProps, prevState) {
        if ( newProps.operation === 'Initial' ) {
            return null;
        }
        else {
            return { operation: newProps.operation };
        }
    }

    generate(elements = 15) {
        this.reset();
        
        for (let i = 0; i < elements; ++i) {
            let val = randomIntFromInverval(1, 150);
            if ( this.insert( val ) ) {
                if ( this.height() > 5 ) {
                    this.root = this.remove( val );
                    --i;
                }
            }
            else {
                --i;
            }
        }
    }

    reset() {
        this.root = null;
    }

    height(node = this.root) {
        if (node == null) return 0;
        return Math.max( this.height(node.left), this.height(node.right) ) + 1;
    }

    insert(val) {
        if (this.root == null) {
            this.root = new BinarySearchTreeNode(val);
            return true;
        }
        
        let curr = this.root;

        while (true) {
            if (curr.value === val) {
                //tree contains key already
                return false;
            }
            else if (curr.value < val) {
                if(curr.right == null) {
                    curr.right = new BinarySearchTreeNode(val);
                    return true;
                }
                curr = curr.right;
            }
            else {
                if(curr.left == null) {
                    curr.left = new BinarySearchTreeNode(val);
                    return true;
                }
                curr = curr.left;
            }
        }
    }

    remove(val, node = this.root) {
        if ( node === null ) return node;

        if ( val < node.value ) {
            node.left = this.remove(val, node.left);
        }
        else if ( val > node.value ) {
            node.right = this.remove(val, node.right);
        }
        else {
            if ( node.left === null ) {
                return node.right;
            }
            else if ( node.right === null ) {
                return node.left;
            }
            else {
                node.value = this.inOrderSuccessor(node.right);
                node.right = this.remove(node.value, node.right);
            }
        }

        return node;
    }

    inOrderSuccessor(node) {
        if ( node === null ) {
            return node;
        }
        else if ( node.left !== null ) {
            return this.inOrderSuccessor(node.left);
        }
        else {
            return node.value;
        }
    }

    getScaleFactor() {
        let scale_factor = (window.innerWidth - 20) / ( Math.pow(2, this.height() - 1) * 70 );
        return 'scale(' + (scale_factor > 1 ? 1 : scale_factor) + ')';
    }

    Tree(node = this.root, tree_height = this.height(), curr_height = 0, pos = 'root', time = 300) {
        if (curr_height === tree_height) return null;

        const leftChild  = this.Tree(node ? node.left  : null, tree_height, curr_height + 1, 'left', time + 500 );
        const rightChild = this.Tree(node ? node.right : null, tree_height, curr_height + 1, 'right', time + 700 );
        
        const classes = ['node-wrapper', pos].join(' ');

        if ( node == null ) {
            return (
                <>
                    <div
                        className={classes}
                        node-value='null'
                        node-layer={tree_height - curr_height}>

                            <div className='node-value'>
                                {getCricle('null')}
                            </div>

                            {leftChild}
                            {rightChild}
                    </div>
                </>
            );
        }
        else {
            return (
                <>
                    <CSSTransition
                        in={true}
                        timeout={time}
                        appear={true}
                        classNames={{
                            appearDone: 'visible'
                        }}>

                        <div
                            className={classes}
                            node-value={node.value}
                            node-layer={tree_height - curr_height}
                            style={ ( node === this.root ) ? {width: Math.pow(2, tree_height - 1) * 70 + 'px'} : null }>

                                <div className='node-value'>
                                    
                                    <span className='node-hover'>
                                        <span className='search' onClick={() => this.searchForNode(node.value)}>
                                            <i class="fas fa-search" />
                                        </span>

                                        <span className='trash' onClick={() => this.trashNode(node.value)}>
                                            <i class="fas fa-trash" />
                                        </span>
                                    </span>

                                    {getCricle(node.value)}

                                </div>

                                {leftChild}
                                {rightChild}

                                {getConnectingLine(pos)}

                        </div>

                    </CSSTransition>
                </>
            );
        }
    }

    render() {
        return (
            <>
                { this.state.input ? <InputPrompt tree = {this} /> : null }

                <div
                    id={ this.name }
                    className='tree'
                    style={ {transform: this.getScaleFactor()} }>

                    { this.Tree() }

                </div>
            </>
        );
    }

    componentDidUpdate() {
        //console.log(this.state);
    }
}

function getCricle(val) {
    return (
        <>
            <svg className='circle node-value-circle' xmlns='http://www.w3.org/2000/svg'>
                <text className='text node-value-text' x='50%' y='50%' fill='black' textAnchor='middle' dominantBaseline='central'>
                    {val}
                </text>
                <circle className='circle node-circle' r='24' cx='25' cy='25' stroke='black' fill='none' />
            </svg>
        </>
    );
}

function getConnectingLine(pos) {
    if (pos === 'left') {
        return (
            <>
                <svg className='line node-line' xmlns='http://www.w3.org/2000/svg'>
                    <line className='line' x1='0' y1='50' x2='100%' y2='0'></line>
                </svg>
            </>
        );
    }
    else if (pos === 'right') {
        return (
            <>
                <svg className='line node-line' xmlns='http://www.w3.org/2000/svg'>
                    <line className='line' x1='0' y1='0' x2='100%' y2='50'></line>
                </svg>
            </>
        );
    }
    else {
        return null;
    }
}
