import React from 'react';
import './TreeVisualizer.css';

export default class TreeVisualizer extends React.Component {
    constructor(props) {
        super(props);
        
        this.state = {
            array: [],
            title: "test"
        };
    }

    render() {
        const {array} = this.state;

        return buildTreeInDOM();
    }

    componentDidMount() {
        this.resetArray(this.state);
    }

    resetArray() {
        const array = [];
        let title = "test2";
        for (let i = 0; i < 100; i++) {
            array.push(randomIntFromInverval(5,1000));
        }
        this.setState({array, title});
    }

    buildTreeInDOM(state) {
        return (
            <>
                {array.map((value,idx) => (
                    <div ClassName="array-bar" arr_key={idx}>
                        {value}
                    </div> 
                ))}
            </>
        );
    }

}

function randomIntFromInverval(min, max) {
    return Math.floor(Math.random() * (max - min + 1) + min);
}
