import React from 'react';
import Queue from '../../Queue.jsx'
import $ from 'jquery'
import { randomIntFromInverval } from '../../Helpers.js'
import { myMax } from '../../Helpers.js'
import './BinarySearchTree.css'

class BinarySearchTreeNode {
    constructor(val) {
        this.value = val;
        this.left  = null;
        this.right = null;
    }
}

export default class BinarySearchTree {
    constructor() {
        this.name = "BinarySearchTree";
        this.root = null;
    }

    generateNewTree(elements = 5) {
        this.root = null;
        $('.node-value').removeClass('visible');
        
        for (let i = 0; i < elements; ++i) {
            this.insert( randomIntFromInverval(1, 200) );
        }
    }

    height(node = this.root) {
        if (node == null) return 0;
        return myMax( this.height(node.left), this.height(node.right) ) + 1;
    }

    insert(val) {
        if (this.root == null) {
            this.root = new BinarySearchTreeNode(val);
            return true;
        }
        
        let curr = this.root;

        while (true) {
            if (val < curr.value) {
                if(curr.left == null) {
                    curr.left = new BinarySearchTreeNode(val);
                    break;
                }
                curr = curr.left;
            }
            else {
                if(curr.right == null) {
                    curr.right = new BinarySearchTreeNode(val);
                    break;
                }
                curr = curr.right;
            }
        }
    }

    animateNodes() {
        let queue = new Queue();
        queue.push( $('.node-wrapper:not([node-value="null"])>.node-value:not(.visible)').first() );
        animateHelper(queue);
    }

    drawTree(node = this.root, tree_height = this.height(), curr_height = 0, pos = "root") {
        if (curr_height === tree_height) return null;

        const leftChild  = this.paint(node ? node.left  : null, tree_height, curr_height + 1, "left" );
        const rightChild = this.paint(node ? node.right : null, tree_height, curr_height + 1, "right");
        
        let classes = ["node-wrapper", pos].join(' ');

        if ( node == null ) {
            return (
                <>
                    <div
                        className={classes}
                        node-value="null"
                        node-layer={tree_height - curr_height}>
                            <div className="node-value">null</div>
                            {leftChild}
                            {rightChild}
                    </div>
                </>
            );
        }
        else if ( node === this.root ) {
            let scale_factor = window.innerWidth / ( Math.pow(2, tree_height - 1) * 70 );
            scale_factor = scale_factor > 1 ? 1 : scale_factor;

            return (
                <>
                    <div
                        className={classes}
                        node-value={node.value}
                        node-layer={tree_height - curr_height}
                        style={ {width: Math.pow(2, tree_height - 1) * 70 + 'px',
                                 transform: "scale(" + scale_factor + ")"} }>

                            <div className="node-value">{node.value}</div>
                            {leftChild}
                            {rightChild}

                    </div>
                </>
            );
        }
        else {
            return (
                <>
                    <div
                        className={classes}
                        node-value={node.value}
                        node-layer={tree_height - curr_height}>

                            {getConnectionLine(pos)}

                            <div className="node-value">{node.value}</div>
                            {leftChild}
                            {rightChild}
                            
                    </div>
                </>
            );
        }
    }
}

function getConnectingLine(pos) {
    if (pos === "left") {
        return (
            <>
                <svg className="line node-line" xmlns="http://www.w3.org/2000/svg">
                    <line class="line" x1="0" y1="50" x2="100%" y2="0"></line>
                </svg>
            </>
        );
    }
    else if (pos === "right") {
        return (
            <>
                <svg className="line node-line" xmlns="http://www.w3.org/2000/svg">
                    <line class="line" x1="0" y1="50" x2="100%" y2="0"></line>
                </svg>
            </>
        );
    }
    else {
        return null;
    }
}

function animateHelper(queue) {
    if ( !queue.empty() ) {
        let $_node = queue.front();
        $_node.addClass('visible');
        $_node.siblings('.node-wrapper:not([node-value="null"])').children('.node-value').each(function() {
           queue.push( $(this) ); 
        });
        setTimeout(function(){animateHelper(queue);}, 300);
    }
}
