import React from 'react';
import logo from './logo.png'
import $ from 'jquery'
import { forEach } from '../Helpers.js'
import '../../../node_modules/font-awesome/css/font-awesome.min.css';
import './NavigationBar.css';

export default class NavigationBar extends React.Component {
    constructor(props) {
        super(props);

        this.visualizer = this.props.visualizer;
        
        this.trees = {
            "BST" : "Binary Search Tree",
            "AVL" : "AVL Tree",
            "RBT" : "Red-Black Tree",
            "BHP" : "Binary Heap"
        };

        this.operations = {
            "BST" : [ "Insert",
                      "Remove",
                      "Search"
                    ],
            "AVL" : [ "Insert",
                      "Remove",
                      "Search"
                    ],
            "RBT" : [ "Insert",
                      "Remove",
                      "Search"
                    ],
            "BHP" : [ "Insert",
                      "Top"
                    ]
        };

        this.state = {
            active: "BST"
        };

        this.generateTree = this.generateTree.bind(this);
        this.resetTree = this.resetTree.bind(this);
    }

    changeTree(tree_key, visualizer) {
        this.setState(
            { active: tree_key }
        );

        this.closeAllDropdowns();

        visualizer.changeTree(tree_key);
    }

    generateTree() {
        this.visualizer.generateNewTree();
    }

    resetTree() {
        this.visualizer.resetTree();
    }

    performOperation(operation, visualizer) {
        this.closeAllDropdowns();

        if ( this.operations[ this.state.active ].includes(operation) ) {
            switch( operation ) {
                case "Insert":
                    visualizer.insert( this.askInput(operation) );
                    break;
                case "Remove":
                    visualizer.remove( this.askInput(operation) );
                    break;
                case "Find":
                    visualizer.find( this.askInput(operation) );
                    break;
                case "Top":
                    visualizer.top();
                    break;
            }
        }
        else {
            //state error.. force update?
        }
    }

    askInput(operation) {
        return 4;
    }

    openDropdown(selector) {
        this.closeAllDropdowns();
        document.getElementById(selector).classList.add('visible');
    }

    closeAllDropdowns() {
        forEach(
            document.getElementsByClassName('dropdown'),
            function(x) {x.classList.remove('visible');}
        );
    }

    render() {
        return (
            <>
                <div id="navbar-wrapper">
                    <div className="navbar-logo">
                        <img src={logo} alt="logo" />
                    </div>
                    
                    <div id="tree-dropdown" className="dropdown">
                        <a
                            className="dropdown-toggle"
                            href="#"
                            onClick={() => this.openDropdown("tree-dropdown")}>
                            {this.trees[ this.state.active ]}
                            <i className="fa fa-caret-down"></i>
                        </a>

                        <ul className="dropdown-menu">
                            {Object.keys(this.trees).map((key, idx) => (
                                <li className="dropdown-menu-option">
                                    <a
                                        href="#"
                                        onClick={(e) => this.changeTree(key, this.visualizer)}>
                                        {this.trees[key]}
                                    </a>
                                </li>
                            ))}
                        </ul>
                    </div>

                    <div id="operations-dropdown" className="dropdown">
                        <a
                            className="dropdown-toggle"
                            href="#"
                            onClick={() => this.openDropdown("operations-dropdown")}>
                            Operations
                            <i className="fa fa-caret-down"></i>
                        </a>

                        <ul className="dropdown-menu">
                            {this.operations[ this.state.active ].map((value, idx) => (
                                <li className="dropdown-menu-option">
                                    <a
                                        href="#"
                                        onClick={() => this.performOperation(value, this.visualizer)}>
                                        {value}
                                    </a>
                                </li>
                            ))}
                        </ul>
                    </div>

                    <div id="generate-tree" className="navbar-button">
                        <button
                            onClick={() => this.generateTree()}>
                            Generate
                        </button>
                    </div>

                    <div id="reset-tree" className="navbar-button">
                        <button
                            onClick={() => this.resetTree(this.visualizer)}>
                            Reset
                        </button>
                    </div>
                </div>
            </>
        );
    }
}
