import React from 'react';
import BinaryTree from './BinaryTree.jsx'
import './TreeVisualizer.css';


export default class TreeVisualizer extends React.Component {
    constructor(props) {
        super(props);
        
        this.state = {
            tree: new BinaryTree()           
        };

        //this.state.tree.insert(1);
        //this.state.tree.insert(3);
        //this.state.tree.insert(5);
        //this.state.tree.insert(7);
        this.state.tree.insert( randomIntFromInverval(1, 20) );
        this.state.tree.insert( randomIntFromInverval(1, 20) );
        this.state.tree.insert( randomIntFromInverval(1, 20) );
        this.state.tree.insert( randomIntFromInverval(1, 20) );
    }

    render() {
        return this.state.tree.createDOM();
    }

    componentDidMount() {
        this.resetArray();
    }

    resetArray() {
        this.state.tree.resetTree();
    }
}

/*function randomIntFromInverval(min, max) {
    return Math.floor(Math.random() * (max - min + 1) + min);
}*/

