import React from 'react';

import NavigationBar from './NavigationBar/NavigationBar.jsx'
import Tooltips from './Tooltips/Tooltips.jsx'

import DefaultTree from './Trees/DefaultTree/DefaultTree.jsx'
import BinarySearchTree from './Trees/BinarySearchTree/BinarySearchTree.jsx'

import './TreeVisualizer.scss';


export default class TreeVisualizer extends React.Component {
    constructor(props) {
        super(props);
        
        this.state = {
            tree: 'BST',
            operation: 'Initial',
            tooltips: false
        };

        this.performOperation = this.performOperation.bind(this);
        this.toggleTooltips   = this.toggleTooltips.bind(this);
        this.changeTree       = this.changeTree.bind(this);
    }

    performOperation(operation) {
        switch ( operation ) {
            case 'Generate':
                this.setState({ operation: operation });
                break;
            case 'Reset':
                this.setState({ operation: operation });
                break;
            case 'Insert':
                this.setState({ operation: operation });
                break;
            default:
                break;
        }
    }

    toggleTooltips() {
        this.setState({ tooltips: ! this.state.tooltips });
    }

    changeTree(tree_key) {
        //this.setState({ operation: 'Reset' });
        this.setState({ tree: tree_key });
    }

    Tree() {
        return (
            <>
                { this.state.tree === 'BST' ?
                    <BinarySearchTree operation = {this.state.operation} />
                : <DefaultTree /> }
            </>
        );
    }

    render() {
        console.log("Visualizer::render: " + this.state.operation);
        return (
            <>
                <NavigationBar visualizer = {this} />
                
                { this.Tree() }

                <Tooltips active = {this.state.tooltips} />
            </>
        );
    }

    shouldComponentUpdate(nextProps, nextState) {
        console.log("Visualizer::shouldComponentUpdate: " + nextState.operation);
        if ( nextState.operation === 'Initial' ) {
            return false;
        }
        else {
            return true;
        }
    }

    componentDidUpdate() {
        console.log("Visualizer::componentDidUpdate: " + this.state.operation);
        if ( this.state.operation !== 'Initial' ) {
            this.setState({ operation: 'Initial' });
        }
    }
}
