import React from 'react';
import './TreeVisualizer.css';

export default class TreeVisualizer extends React.Component {
    constructor(props) {
        super(props);
        
        this.state = {
            array: [],
            title: "test"
        };
    }

    render() {
        return buildTreeInDOM(this.state);
    }

    componentDidMount() {
        this.resetArray();
    }

    resetArray() {
        const array = [];
        let title = "test2";
        for (let i = 0; i < 100; i++) {
            array.push(randomIntFromInverval(5,1000));
        }
        this.setState({array, title});
    }

}

function randomIntFromInverval(min, max) {
    return Math.floor(Math.random() * (max - min + 1) + min);
}

function buildTreeInDOM(state) {
    const {array} = state;

