import React from 'react';
import NavigationBar from './NavigationBar/NavigationBar.jsx'
import NULLTree from './Trees/NULLTree/NULLTree.jsx'
import BinarySearchTree from './Trees/BinarySearchTree/BinarySearchTree.jsx'
import './TreeVisualizer.css';


export default class TreeVisualizer extends React.Component {
    constructor(props) {
        super(props);
        
        this.state = {
            tree: new BinarySearchTree(),
            operation: "initial",
            tooltips: false
        };

        this.generateTree = this.generateTree.bind(this);
        this.resetTree    = this.resetTree.bind(this);
        this.insert       = this.insert.bind(this);
        this.remove       = this.remove.bind(this);
        this.search       = this.search.bind(this);
        this.top          = this.top.bind(this);
        this.preOrder     = this.preOrder.bind(this);
        this.inOrder      = this.inOrder.bind(this);
        this.postOrder    = this.postOrder.bind(this);
        this.levelOrder   = this.levelOrder.bind(this);
    }

    toggleTooltips() {
        this.setState( { tooltips: ! this.state.tooltips } );
    }

    changeTree(tree_key) {
        switch(tree_key) {
            case "BST": this.setState( { tree: new BinarySearchTree() } );
                break;
            case "AVL":
                break;
            case "RBT":
                break;
            case "BHP":
                break;
            default: this.setState( { tree: new NULLTree() } );  //show error, invalid tree
                break;
        }
    }

    generateTree() {
        this.state.tree.generateTree();
        this.setState( { operation: "generate" } );
    }

    resetTree() {
        this.state.tree.reset();
        this.setState( { operation: "reset" } );
    }

    insert() {
        this.state.tree.insert(4)
        this.setState( { operation: "insert" } );
    }

    remove() {
        this.setState( { operation: "remove" } );
    }

    search() {
        this.setState( { operation: "search" } );
    }

    top() {
        this.setState( { operation: "top" } );
    }

    preOrder() {

    }

    inOrder() {
        
    }

    postOrder() {
        
    }

    levelOrder() {
        
    }

    Tree() {
        return this.state.tree.Tree();
    }

    render() {
        return (
            <>
                <NavigationBar visualizer = {this} />
                
                <div
                    id={ this.state.tree.name }
                    style={ {transform: "scale(" + this.state.tree.getScaleFactor() + ")"} }
                    className="tree">
                        { this.Tree() }
                </div>

                { this.state.tooltips ? (
                    <>
                    </>
                ) : ( null ) }
            </>
        );
    }

    componentDidUpdate() {
        this.state.tree.animateNodes();
    }
}
