import React from 'react';
import BinaryTree from './BinaryTree.jsx'
import './TreeVisualizer.css';


export default class TreeVisualizer extends React.Component {
    constructor(props) {
        super(props);
        
        this.state = {
            tree: new BinaryTree(),
            title: "test"
        };
    }

    render() {
        return buildTreeInDOM(this.state);
    }

    componentDidMount() {
        this.resetArray();
    }

    resetArray() {
        const array = [];
        let title = "test2";
        for (let i = 0; i < 100; i++) {
            array.push(randomIntFromInverval(5,1000));
        }
        this.setState({array, title});
    }
}

function randomIntFromInverval(min, max) {
    return Math.floor(Math.random() * (max - min + 1) + min);
}

function buildTreeInDOM(state) {
    /*const {array} = state;
    return (
        <>
            {array.map((value,idx) => (
                <div ClassName="array-bar" arr_key={idx}>
                    {value}
                </div> 
            ))}
        </>
    );*/

    return (
        <>
            <div className="node">
                <div className="node-value">3</div>
                <div className="node">
                    <div className="node-value">4</div>
                </div>
                <div className="node">
                    <div className="node-value">5</div>
                </div>
            </div>
        </>
    );
}

