import React from 'react';

class BinaryNode {
    constructor(val) {
        this.value = val;
        this.left  = null;
        this.right = null;
    }

    left() {
        return this.left;
    }

    right() {
        return this.right;
    }

}

export default class BinaryTree {
    constructor() {
        this.root = null;
    }

    insert(val) {
        if (this.root == null) {
            this.root = new BinaryNode(val);
        }

        let curr = this.root;

        while (curr != null) {
            if (val < curr.value) {
                curr = curr.left();
            }
            else {
                curr = curr.right();
            }
        }

        curr = new BinaryNode(val);
    }

    inOrderArray() {
        const obj = {array:[]};
        inOrder(this.root, obj);
        return obj;
    }

    inOrder(node, obj) {
        if(node == null) {
            return;
        }

        inOrder(node.left(), obj);
        obj.array.push(node.value);
        inOrder(node.right(), obj);

        return;
    }

}
