import React from 'react';
import{ CSSTransition } from 'react-transition-group'

import './Tooltips.scss';

export default class Tooltips extends React.Component {
    constructor(props) {
        super(props);

        this.visibleClasses = {
            appearDone: 'visible',
            enterDone: 'visible',
            exit: 'visible'
        };

        this.state = {
            active: this.props.active,
            operation: this.props.operation,
            step: this.props.step,
            timeout: this.props.timeout
        };
    }

    static getDerivedStateFromProps(newProps, state) {
        if ( state.active !== newProps.active || state.operation !== newProps.operation || state.step !== newProps.step ) {
            return {
                active: newProps.active,
                operation: newProps.operation,
                step: newProps.step,
                timeout: newProps.timeout
            };
        }
        else {
            return null;
        }
    }

    getContent() {
        return (
            <>
                <div id="tooltips">
                    <div>Tooltips active</div>
                    <div>{this.state.operation} : {this.state.step}</div>

                    <CSSTransition
                        in={this.state.active}
                        timeout={this.state.timeout}
                        classNames={this.visibleClasses}
                        appear={true}
                        key={this.state.operation + this.state.step}>

                        <button className='tooltips-next' onClick={this.props.next}>Next</button>

                    </CSSTransition>

                </div>
            </>
        );
    }

    render() {
        if ( this.state.active ) {
            //if ( this.state.timeout === 0 && this.state.operation !== 'Initial' ) this.props.next();

            return (
                <>
                    {this.getContent()}
                </>
            );
        }
        else {
            return null;
        }
    }
}
