import React from 'react';
import logo from './logo.png'
import { forEach } from '../Helpers.js'
import '../../../node_modules/font-awesome/css/font-awesome.min.css';
import './NavigationBar.css';

export default class NavigationBar extends React.Component {
    constructor(props) {
        super(props);

        this.visualizer = this.props.visualizer;
        
        this.trees = {
            "BST" : "Binary Search Tree",
            "AVL" : "AVL Tree",
            "RBT" : "Red-Black Tree",
            "BHP" : "Binary Heap"
        };

        this.operations = {
            "BST" : [ "Insert",
                      "Remove",
                      "Search"
                    ],
            "AVL" : [ "Insert",
                      "Remove",
                      "Search"
                    ],
            "RBT" : [ "Insert",
                      "Remove",
                      "Search"
                    ],
            "BHP" : [ "Insert",
                      "Top"
                    ]
        };

        this.state = {
            active: "BST"
        };
    }

    changeTree(tree_key, visualizer) {
        this.setState(
            { active: tree_key }
        );

        this.closeAllDropdowns();

        visualizer.changeTree(tree_key);
    }

    generateTree(visualizer) {
        visualizer.initialize();
    }

    insertElement(visualizer) {
        visualizer.insert(5);
    }

    openDropdown(selector) {
        this.closeAllDropdowns();
        document.getElementById(selector).getElementsByClassName('dropdown-menu')[0].classList.add('visible');
    }

    closeAllDropdowns() {
        forEach(
            document.getElementsByClassName('dropdown-menu'),
            function(x) {x.classList.remove('visible');}
        );
    }

    render() {
        return (
            <>
                <div id="navbar-wrapper">
                    <div className="navbar-logo">
                        <img src={logo} alt="logo" />
                    </div>
                    
                    <div id="tree-dropdown" className="dropdown">
                        <a
                            className="dropdown-toggle"
                            href="#"
                            onClick={() => this.openDropdown("tree-dropdown")}>
                            {this.trees[ this.state.active ]}
                            <i className="fa fa-caret-down"></i>
                        </a>

                        <ul className="dropdown-menu">
                            {Object.keys(this.trees).map((value, idx) => (
                                <li className="dropdown-menu-option">
                                    <a
                                        href="#"
                                        onClick={() => this.changeTree(key, this.visualizer)}>
                                        {value}
                                    </a>
                                </li>
                            ))}
                        </ul>
                    </div>



                    <div id="operations-dropdown" className="dropdown">
                        <a
                            className="dropdown-toggle"
                            href="#"
                            onClick={() => this.openDropdown("operations-dropdown")}>
                            Operations
                            <i className="fa fa-caret-down"></i>
                        </a>

                        <ul className="dropdown-menu">
                            {this.operations[ this.state.active ].map((key, idx) => (
                                <li className="dropdown-menu-option">
                                    <a
                                        href="#"
                                        onClick={() => this.changeTree(key, this.visualizer)}>
                                        {this.trees[key]}
                                    </a>
                                </li>
                            ))}
                        </ul>
                    </div>




                    <div id="generate-tree">
                        <button
                            onClick={() => this.generateTree(this.visualizer)}>
                            Generate
                        </button>
                    </div>

                    <div id="insert-element">
                        <button
                            onClick={() => this.insertElement(this.visualizer)}>
                            Insert
                        </button>
                    </div>
                </div>
            </>
        );
    }
}
